package com.api.parkingcontrol.models;import jakarta.persistence.*;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.io.Serializable;import java.util.Collection;import java.util.Collections;import java.util.UUID;@Entity@Table(name = "TB_USER")public class UserModel implements UserDetails,Serializable {    public static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID userId;    @Column(nullable = false, unique = true)    private String username;    @Column(nullable = false)    private String password;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return Collections.singleton(new SimpleGrantedAuthority("ADMIN"));    }    @Override    public String getPassword() {        return this.password;    }    @Override    public String getUsername() {        return this.username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }}